public class Calculatefact {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int a=5;
        long fact=4;
        for(int z=4;z<=a;z++)
        {
        	fact=fact*z; //1,2,6,24
        }
        System.out.println("factorial:"+fact);
        
        }
        	
        	
        
	}


